mapper.update:
UPDATE NOTICE SET 
 VIEW_COUNT = VIEW_COUNT+1 
WHERE NOTICE_ID=#{noticeId}

     UPDATE notice
	  SET
     title = #{title},
     content = #{content},
     CATEGORY_ID=#{categoryId}
    where notice_id=#{noticeId}

mapper.insert:

SELECT NOTICE_SEQ.NEXTVAL FROM dual


INSERT INTO notice (
    notice_id
    , emp_id
    , title
    , content
    , category_Id
    , created_at
) VALUES (
	#{noticeId}
	,#{empId}
	,#{title}
	,#{content}
	,#{categoryId}
	,sysdate
)
