mapper.update:
     UPDATE notice
	  SET
     title = #{title},
     content = #{content}
    where notice_id=#{noticeId}

mapper.insert:
INSERT INTO notice (
    notice_id
    , emp_id
    , title
    , content
    , category_Id
    , created_at
) VALUES (
	NOTICE_SEQ.NEXTVAL
	,#{empId}
	,#{title}
	,#{content}
	,#{categoryId}
	,sysdate
)
